jQuery(function($){$("body").bind("wc_gzdp_step_changed",function(){if($(".step-wrapper-active").attr("id")==="step-wrapper-payment"){var current=$("form.woocommerce-checkout").find(".wc-gzdp-payment-method-placeholder").data("current");if(current&&$("form.woocommerce-checkout").find("input#"+current).length>0){$("form.woocommerce-checkout").find("input#"+current).prop("checked",true)}$("form.woocommerce-checkout").find(".wc-gzdp-payment-method-placeholder").remove()}});$(document).on("refresh",".step-wrapper",function(){if($(this).attr("id")==="step-wrapper-payment"){}else{var $current=$("input[name=payment_method]:checked");if($("form.woocommerce-checkout").find(".wc-gzdp-payment-method-placeholder").length==0){var id=$current.length>0?$current.attr("id"):"";$("form.woocommerce-checkout").append('<input type="radio" style="display: none;" name="payment_method" data-current="'+id+'" class="wc-gzdp-payment-method-placeholder" value="placeholder" checked="checked" />')}}})});